
public class Q7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=5;
		int y=10;
		int z;
		System.out.println("Before swapping a value of x= "+x+" and value of y= "+y);
		z=x;
		x=y;
		y=z;
		System.out.println("After swapping a value of x= "+x+" and value of y= "+y);

	}

}
