
public class Q8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=10;
		int y=20;
		System.out.println("Before swapping a value of x= "+x+" and value of y= "+y);
		x=x+y;    //10+20=30
		y=x-y;    //30-20=10
		x=x-y;    //30-10=20
		System.out.println("After swapping a value of x= "+x+" and value of y= "+y);


	}

}
